<template>  <div class="activities">    <div class="carousel">      <div class="carousel-inner" :style="{ transform: `translateX(-${currentIndex * 100}%)` }">        <div class="carousel-item" v-for="(activity, index) in activities" :key="index">          <img :src="activity.image" :alt="activity.title" class="activity-image" />          <div class="activity-info">            <h3>{{ activity.title }}</h3>            <p>{{ activity.description }}</p>          </div>        </div>      </div>      <button class="carousel-control prev" @click="prevSlide">‹</button>      <button class="carousel-control next" @click="nextSlide">›</button>    </div>    <div class="carousel-indicators">      <span v-for="(activity, index) in activities" :key="index" :class="{ active: currentIndex === index }" @click="goToSlide(index)"></span>    </div>  </div></template><script>export default {  data() {    return {      currentIndex: 0,      activities: [        {          image: '/img/2024bucpc.png',          title: '2024“札记杯”宁波大学第二十一届大学生程序设计竞赛',          description: '编程乐趣？我们满足你！增强问题分析与解决能力，提升团队之间的协作能力，享受算法带来的乐趣！',        },        {          image: '/img/summertrain.png',          title: '2023年程序设计协会暑期集训',          description: '宁波大学ACM集训队举办的2023年暑期集训，提升编程技能，备战各类编程竞赛。',        },        {          image: '/img/4.png',          title: '2024年宁波大学程序设计大赛',          description: '宁波大学ACM集训队组织、参与2024年宁波大学程序设计大赛。',        },        {          image: '/img/3.png',          title: '2023-2024学年协会日常集训',          description: '宁波大学ACM集训队组织日常集训',        },      ],      autoPlayInterval: null,    };  },  methods: {    nextSlide() {      this.currentIndex = (this.currentIndex + 1) % this.activities.length;    },    prevSlide() {      this.currentIndex = (this.currentIndex - 1 + this.activities.length) % this.activities.length;    },    goToSlide(index) {      this.currentIndex = index;    },    startAutoPlay() {      this.autoPlayInterval = setInterval(this.nextSlide, 3000);    },    stopAutoPlay() {      clearInterval(this.autoPlayInterval);      this.autoPlayInterval = null;    },  },  mounted() {    this.startAutoPlay();  },  beforeUnmount() {    this.stopAutoPlay();  },};</script><style scoped>.activities {  width: 100%;  max-width: 800px;  margin: 0 auto;  position: relative;}.carousel {  position: relative;  overflow: hidden;}.carousel-inner {  display: flex;  transition: transform 1s ease;}.carousel-item {  min-width: 100%;  box-sizing: border-box;}.activity-image {  width: 800px;  height: 600px;  object-fit: cover;  border-radius: 8px;}.activity-info {  padding: 10px;  text-align: center;}.activity-info h3 {  margin: 10px 0;}.activity-info p {  color: #666;}.carousel-control {  position: absolute;  top: 45%;  transform: translateY(-50%);  background-color: rgba(0, 0, 0, 0.5);  color: white;  border: none;  font-size: 2em;  cursor: pointer;  padding: 10px;  border-radius: 50%;}.carousel-control.prev {  left: 10px;}.carousel-control.next {  right: 10px;}.carousel-indicators {  display: flex;  justify-content: center;  margin-top: 10px;}.carousel-indicators span {  width: 10px;  height: 10px;  margin: 0 5px;  background-color: #ccc;  border-radius: 50%;  display: inline-block;  cursor: pointer;}.carousel-indicators span.active {  background-color: #333;}</style>